import React, { useState } from "react";
import "./Register.css";

export default function Register() {
    const [fullName,setFullName]=useState("");
    const [email,setEmail]=useState("");
    const [password,setPassword]=useState("");
    const [confirmPassword,setConfirmPassword]=useState("");
    const [phoneNumber,setPhoneNumber]=useState();
    const [dob,setDob]=useState();

    //function to handle input
    const handleChange=(event)=>{
        event.preventDefault();
        const {name,value}=event.target;
        if (name === "name") setName(value);
        if (name === "email") setEmail(value);
        if (name === "password") setPassword(value);
        if (name === "confirmPassword") setConfirmPassword(value);
        if (name === "phoneNumber") setPhoneNumber(value);
        if (name === "dob") setDob(value);
    };

  return (
    <div>
      <div className="register-head">
        <h1>Register</h1>
      </div>
      <form>
        <label>
          Enter Full Name
          <input type="text" placeholder="Enter Full Name" name="fullName" value={fullName} onChange={()=>{handleChange()}}/>
        </label>
        <label>
          Email Address
          <input type="text" placeholder="Enter Email" name="email" value="email" onChange={()=>{handleChange()}}/>
        </label>
        <label>
          Enter Password
          <input type="text" placeholder="Enter Password" name="password" value={password} onChange={()=>{handleChange()}}/>
        </label>
        <label>
          Confirm Password
          <input type="text" placeholder="Re Enter Password" name="confirmPassword" value={confirmPassword} onChange={()=>{handleChange()}} />
        </label>
        <label>
          Enter Phone Number
          <input type="text" placeholder="Phone Number" name="phoneNumber" value={phoneNumber} onChange={()=>{handleChange()}} />
        </label>
        <label>
          Enter Date of Birth
          <input type="date" placeholder="Enter Date Of Birth" name="dob" value={dob} onChange={()=>{handleChange()}} />
        </label>
      </form>
    </div>
  );
}
