import React, { useState } from 'react'
import {useDispatch,useSelector} from 'react-redux'
import {uploadImage} from '../../state/slicer.js'

export default function ImageUpload() {
    const [imagePreview,setImagePreview]=useState(null);
    const [file,setFile]=useState()
    const [title,setTitle]=useState('');
    const [description,setDescription]=useState('');

    const dispatch=useDispatch()
    const isSuccess = useSelector((state) => state.uploadImage?.isSuccess);

    const handleFileChange = (e) => {
        setImagePreview(URL.createObjectURL(e.target.files[0]));
        FileSystemFileEntry(e.target.files[0])
      };
    
    const handleUploadImage=(e)=>{
        e.preventDefault()
        if(file&&title&&description){
            const formData = new FormData();
            formData.append('file', file);
            formData.append('title', title);
            formData.append('description', description);
            dispatch(uploadImage({formData}))
        }
    }


  return (
    <div>
        <h2>Upload Image</h2>
      <form>
        <label>
            Chose image
            <input
            type='file'
            onChange={(event)=>handleFileChange(event)}
            />
        </label>
        <label>
            Title
            <input
            type='text'
            value={title}
            onChange={(e)=>setTitle(e.target.value)}
            />
        </label>
        <label>
            Description
            <input
            type='text'
            value={description}
            onChange={(e)=>setDescription(e.target.value)}
            />
        </label>
        {imagePreview && <span>Chosen Image</span>}
        {imagePreview && <img src={imagePreview} alt="Preview" />}
        <div className="button">
            <button onClick={(event)=>{handleUploadImage(event)}}>Upload</button>
        </div>
      </form>
    </div>
  )
}
