import React, { useState,useEffect } from 'react'
import {useDispatch,useSelector} from 'react-redux'
import {uploadImage} from '../../state/slicer.js'
import Navbar from "../Navbar/Navbar.jsx"

export default function ImageUpload() {
    const [imagePreview,setImagePreview]=useState(null);
    const [file,setFile]=useState()
    const [title,setTitle]=useState('');
    const [viewCount,setViewCount]=useState(0)
    const [description,setDescription]=useState('');

    const dispatch=useDispatch()
    const isSuccess = useSelector((state) => state.image?.isSuccess);

    const handleFileChange = (e) => {
        setImagePreview(URL.createObjectURL(e.target.files[0]));
        setFile(e.target.files[0])
      };
    
    const handleUploadImage=async(e)=>{
        e.preventDefault()
        if(file&&title&&description){
            const formData = new FormData();
            formData.append('file', file);
            formData.append('title', title);
            formData.append('viewCount',viewCount);
            formData.append('description', description);
            console.log("formData",formData);
            await dispatch(uploadImage(formData))
            setFile({});
                setTitle('')
                setDescription('')
        }
    }

    useEffect(() => {
        if (isSuccess) {
            console.log('Image uploaded successfully!');
            setFile({});
                setTitle('')
                setDescription('')
                alert("Image uploaded successfully")
        }
    }, [isSuccess]);


  return (
    <>
    <Navbar/>
    <div style={{display:'flex', flexDirection:'column', alignItems:'center', justifyContent:"center" }}>
        <h2>Upload Image</h2>
      <form>
        <label>
            Chose image
            <input
            type='file'
            onChange={(event)=>handleFileChange(event)}
            />
        </label>
        <label>
            Title
            <input
            type='text'
            value={title}
            maxLength={15}
            onChange={(e)=>setTitle(e.target.value)}
            />
        </label>
        <label>
            Description
            <input
            type='text'
            value={description}
            maxLength={250}
            onChange={(e)=>setDescription(e.target.value)}
            />
        </label>
        {imagePreview && <span>Chosen Image</span>}
        {imagePreview && <img src={imagePreview} alt="Preview" />}
        <div className="button" style={{display:'flex', justifyContent:'center', alignItems:'center'}}>
            <button style={{ color:'white', background:'black'}} onClick={(event)=>{handleUploadImage(event)}}>Upload</button>
        </div>
      </form>
    </div>
    </>
  )
}
