import Login from '../Login/Login';
import Register from '../Register/Register';
import Dashboard from '../Dashboard/Dashboard';
import ProtectedRoute from '../ProtectedRoute/ProtectedRoute';
import {BrowserRouter as Router,Route,Routes} from 'react-router-dom';
import './App.css';
import ImageUpload from '../ImageUpload/ImageUpload';
import { Provider } from 'react-redux';
import store from "../../state/store"
import ImageGallary from '../ImageGallary/ImageGallary';
import axios from 'axios';
import { useState,useEffect } from 'react';


function App() {
const [loggedIn,setLoggedIn]=useState(false)

const checkSession = async () => {
  try {
    const response = await axios.get('http://localhost:3001/checkSession', {
      withCredentials: true // ensure cookies are sent with the request
    });

    console.log("")
    setLoggedIn(response.data.loggedIn);
  } catch (error) {
    console.error('Error checking session', error);
    setLoggedIn(false);
  }
};

useEffect(() => {
  checkSession();
}, []);


  return (
    <Provider store={store} >
    <Router>
      <Routes>
        <Route path='/' element={<Login/>}/>
        <Route  element={<Register/>}/>
        <Route  element={<ProtectedRoute path='/Dashboard' element={Dashboard} loggedIn={loggedIn}/>} />
        <Route  element={<ProtectedRoute path='/uploadImage' element={ImageUpload} loggedIn={loggedIn}/>} />
        <Route  element={<ProtectedRoute path='/ImageGallary' element={ImageGallary} loggedIn={loggedIn}/>} />
      </Routes>
    </Router>
    </Provider>
  );
}

export default App;
