import React, { useState,useEffect } from 'react'
import {useDispatch,useSelector} from 'react-redux'
import {uploadImage} from '../../state/slicer.js'

export default function ImageUpload() {
    const [imagePreview,setImagePreview]=useState(null);
    const [file,setFile]=useState()
    const [title,setTitle]=useState('');
    const [description,setDescription]=useState('');

    const dispatch=useDispatch()
    const isSuccess = useSelector((state) => state.image?.isSuccess);

    const handleFileChange = (e) => {
        setImagePreview(URL.createObjectURL(e.target.files[0]));
        setFile(e.target.files[0])
      };
    
    const handleUploadImage=async(e)=>{
        e.preventDefault()
        if(file&&title&&description){
            const formData = new FormData();
            formData.append('file', file);
            formData.append('title', title);
            formData.append('description', description);
            console.log("formData",formData);
            await dispatch(uploadImage(formData))
            if(isSuccess){
                setFile({});
                set
                alert("Image uploaded successfully")
            }
            
        }
    }

    useEffect(() => {
        if (isSuccess) {
            console.log('Image uploaded successfully!');
            // You can add more actions here, like resetting the form or showing a success message
        }
    }, [isSuccess]);


  return (
    <div>
        <h2>Upload Image</h2>
      <form>
        <label>
            Chose image
            <input
            type='file'
            onChange={(event)=>handleFileChange(event)}
            />
        </label>
        <label>
            Title
            <input
            type='text'
            value={title}
            onChange={(e)=>setTitle(e.target.value)}
            />
        </label>
        <label>
            Description
            <input
            type='text'
            value={description}
            onChange={(e)=>setDescription(e.target.value)}
            />
        </label>
        {imagePreview && <span>Chosen Image</span>}
        {imagePreview && <img src={imagePreview} alt="Preview" />}
        <div className="button">
            <button onClick={(event)=>{handleUploadImage(event)}}>Upload</button>
        </div>
      </form>
    </div>
  )
}
